import express from 'express';
import { addInstitution, getInstitutions, deleteInstitution } from '../controllers/institutionController.js';
import multer from 'multer';
const router = express.Router();
const upload = multer({ /* ... your multer config ... */ });

router.get('/', getInstitutions);
router.post('/', upload.single('logo'), addInstitution);
router.delete('/:id', deleteInstitution);

export default router;