import express from 'express';
import { getUsers, registerUser, loginUser, updateUserProfile } from '../controllers/userController.js';
import multer from 'multer'; // ensure multer is imported for file upload handling
const router = express.Router();
const upload = multer({ /* ... your multer config ... */ });

router.get('/', getUsers);
router.post('/register', upload.single('profilePicture'), registerUser);
router.post('/login', loginUser);
router.put('/update/:id', upload.single('profilePicture'), updateUserProfile);

export default router;